CREATE TABLE acao (
	codigo BIGINT PRIMARY KEY AUTO_INCREMENT,
	descricao VARCHAR(50) NOT NULL
);

CREATE TABLE interacao (
	codigo BIGINT PRIMARY KEY AUTO_INCREMENT,
	mecanismo_efeito VARCHAR(500) NOT NULL,
	recomendacoes VARCHAR(700) NOT NULL,
	
	codigo_acao BIGINT NOT NULL,
	codigo_medicamento_a BIGINT NOT NULL,
	codigo_medicamento_b BIGINT NOT NULL,
	
	FOREIGN KEY (codigo_acao) REFERENCES acao(codigo),
	FOREIGN KEY (codigo_medicamento_a) REFERENCES medicamento(codigo),
	FOREIGN KEY (codigo_medicamento_b) REFERENCES medicamento(codigo)
);


