insert into interacao (codigo_medicamento_a, codigo_medicamento_b, codigo_acao, mecanismo_efeito, recomendacoes) values (61, 96, 3, 'Risco de depressão respiratória grave e / ou prolongada. Os aminoglicosídeos inibem a liberação de acetilcolina nas junções neuromusculares, interferindo no influxo de cálcio.', 'Evitar o uso de gentamicina durante e imediatamente após a cirurgia em que os bloqueadores neuromusculares foram empregados. Se usados, os sinais vitais devem ser monitorados e os ajustes de doses devem ser realizados conforme necessidade clínica');
