insert into interacao (codigo_medicamento_a, codigo_medicamento_b, codigo_acao, mecanismo_efeito, recomendacoes) values (9, 73, 5, 'Aumento da concentração plasmática de loperamida no plasma e no sistema nervoso central (SNC), devido a inibição do transportador de efluxo, glicoproteína P (gp-P) causada pela amiodarona. A inibição da P-gp no intestino aumenta a absorção sistêmica da loperamida e facilita a entrada da loperamida no SNC', 'Recomenda-se precaução no uso. Recomenda-se especial cautela no uso da loperamida, se outros medicamentos que inibem outras vias de eliminação da loperamida (CYP450 2C8; CYP450 3A4) também forem usados, pois podem agir sinergicamente com inibidores da P-gp para aumentar as concentrações de loperamida');
